SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_solic_pront_agenda (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_solic_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	/* obter solicitação */

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_solic_w
	from	same_cpi_solic
	where	nr_seq_agenda = nr_seq_agenda_p;

	/* cancelar solicitação */

	if (coalesce(nr_seq_solic_w,0) > 0) then
		update	same_cpi_solic
		set	ie_status_solic	= 'C',
			dt_cancelamento	= clock_timestamp(),
			nm_usuario_cancel	= nm_usuario_p
		where	nr_sequencia		= nr_seq_solic_w
		and	ie_status_solic	= 'A';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
