SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_titulos_lote (ds_lista_titulos_p text, ie_pagar_receber_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w	bigint;

c01 CURSOR FOR 
SELECT	nr_titulo 
from	titulo_receber a 
where	' ' || ds_lista_titulos_p || ' ' like '% ' || to_char(a.nr_titulo) || ' %';


BEGIN 
 
if (ie_pagar_receber_p = 'R') then 
 
	open c01;
	loop 
	fetch c01 into 
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	 
		CALL cancelar_titulo_receber(nr_titulo_w,nm_usuario_p,'N',clock_timestamp());
	end loop;
	close c01;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
