SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_titulo_contrato (nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_pagar_w	titulo_pagar.nr_titulo%type;
nr_titulo_receber_w	titulo_receber.nr_titulo%type;

c01 CURSOR FOR 
SELECT	a.nr_titulo 
from	titulo_pagar a 
where	coalesce(a.vl_saldo_titulo,0)	<> 0 
and	a.nr_seq_contrato		= nr_seq_contrato_p;

c02 CURSOR FOR 
SELECT	a.nr_titulo 
from	titulo_receber a 
where	coalesce(a.vl_saldo_titulo,0)	<> 0 
and	a.nr_seq_contrato		= nr_seq_contrato_p;


BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	nr_titulo_pagar_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	CALL cancelar_titulo_pagar(nr_titulo_pagar_w,nm_usuario_p,clock_timestamp());
 
end	loop;
close	c01;
 
open	c02;
loop 
fetch	c02 into 
	nr_titulo_receber_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
 
	CALL cancelar_titulo_receber(nr_titulo_receber_w,nm_usuario_p,'N',clock_timestamp());
 
end	loop;
close	c02;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cancelar_titulo_contrato (nr_seq_contrato_p bigint, nm_usuario_p text) FROM PUBLIC;

