SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_viagem_gv ( nr_seq_viagem_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_adiantamento_w	double precision;
vl_alimentacao_w	double precision;
ds_usuario_receb_w	varchar(2000);
nm_usuario_receb_w	varchar(15);
ds_mensagem_fin_w	varchar(4000);

c01 CURSOR FOR
SELECT	nm_usuario_receb
from	fin_regra_receb_ci_gv;


BEGIN
select	coalesce(max(a.vl_adiantamento),0),
	coalesce(max(a.vl_alimentacao),0)
into STRICT	vl_adiantamento_w,
	vl_alimentacao_w
from	fin_gv_pend_aprov a
where	a.nr_seq_viagem = nr_seq_viagem_p;

if	((vl_adiantamento_w > 0) or (vl_alimentacao_w > 0)) then
	begin
	ds_usuario_receb_w := '';
	open C01;
	loop
	fetch C01 into
		nm_usuario_receb_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_usuario_receb_w :=  ds_usuario_receb_w || nm_usuario_receb_w || ',';
		end;
	end loop;
	ds_usuario_receb_w := ds_usuario_receb_w || nm_usuario_p;
	close C01;
	ds_mensagem_fin_w := 'GV: ' || nr_seq_viagem_p;
	if (vl_adiantamento_w > 0) then
		ds_mensagem_fin_w := ds_mensagem_fin_w || chr(13) || chr(10) || Wheb_mensagem_pck.get_texto(306142, 'VL_ADIANTAMENTO=' || vl_adiantamento_w); -- Possuí R$ #@VL_ADIANTAMENTO#@ de adiantamento.
	end if;
	if (vl_alimentacao_w > 0) then
		ds_mensagem_fin_w := ds_mensagem_fin_w || chr(13) || chr(10) || Wheb_mensagem_pck.get_texto(306140, 'VL_ALIMENTACAO=' || vl_alimentacao_w); -- Possuí R$ #@VL_ALIMENTACAO#@ de alimentação.
	end if;
	CALL gerar_comunic_padrao(	clock_timestamp(),
				Wheb_mensagem_pck.get_texto(306106, 'NR_SEQ_VIAGEM_P=' || nr_seq_viagem_p), --  Estorno de aprovação da GV: #@NR_SEQ_VIAGEM_P#@
				ds_mensagem_fin_w,
				nm_usuario_p, 'N', ds_usuario_receb_w, 'S', null, '', '', '', clock_timestamp(), '', '');
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
