CREATE OR REPLACE PROCEDURE cancela_obito_pac (cd_pessoa_fisica_p text, dt_obito_p timestamp, nm_usuario_p text) AS $body$
DECLARE


	dt_obito_w	timestamp;

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	Select max(dt_obito)
	into STRICT dt_obito_w
	from pessoa_fisica
	where cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (dt_obito_w = dt_obito_p) then
		update	pessoa_fisica
		set	dt_obito		 = NULL,
		 	nm_usuario		= nm_usuario_p,
		 	dt_atualizacao 		= clock_timestamp()
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	(dt_obito IS NOT NULL AND dt_obito::text <> '');
	end if;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
