CREATE OR REPLACE PROCEDURE cancela_prescr_emerg_palm (nr_prescricao_p bigint) AS $body$
DECLARE


qt_item_prescricao_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select 	count(*)
	into STRICT	qt_item_prescricao_w
	from	prescr_material
	where	nr_prescricao	=	nr_prescricao_p;

	if (qt_item_prescricao_w = 0) then
		delete 	from prescr_medica
		where	nr_prescricao 	=	nr_prescricao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
