CREATE OR REPLACE PROCEDURE cancel_cons_agenda ( nr_seq_agenda_cons_p parecer_medico_req.nr_seq_agenda_cons%type) AS $body$
DECLARE


	nr_sequencia_w		agenda_consulta.nr_sequencia%type;


BEGIN

	begin
		if (nr_seq_agenda_cons_p IS NOT NULL AND nr_seq_agenda_cons_p::text <> '') then

			select	nr_sequencia
			into STRICT	nr_sequencia_w
			from	agenda_consulta
			where	nr_sequencia = nr_seq_agenda_cons_p;

			if ((coalesce(nr_sequencia_w,nr_seq_agenda_cons_p) IS NOT NULL AND (coalesce(nr_sequencia_w,nr_seq_agenda_cons_p))::text <> '')) then
	
				update	agenda_consulta
				set	ie_status_agenda = 'C',
					dt_cancelamento = clock_timestamp(),
					nm_usuario_cancelamento = wheb_usuario_pck.get_nm_usuario,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = wheb_usuario_pck.get_nm_usuario
				where	nr_sequencia = coalesce(nr_sequencia_w,nr_seq_agenda_cons_p);
				
				commit;
			end if;

		end if;
	end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
