CREATE OR REPLACE PROCEDURE canc_agend_futuros (cd_pessoa_fisica_p text, nr_seq_motivo_canc_p bigint) AS $body$
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_motivo_canc_p IS NOT NULL AND nr_seq_motivo_canc_p::text <> '') then

	update	agenda_hc_paciente
	set	ie_status_agenda = 'C',
		nr_seq_motivo_canc = nr_seq_motivo_canc_p
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	dt_agenda >= clock_timestamp();

	commit;

end if;

end;
