CREATE OR REPLACE PROCEDURE canc_nf_estorno_baixa_tit ( nr_seq_nf_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_emissao_w	nota_fiscal.dt_emissao%type;				
				   

BEGIN 
 
select dt_emissao 
into STRICT  dt_emissao_w	 
from  nota_fiscal 
where nr_sequencia = nr_seq_nf_p;
 
if (PKG_DATE_UTILS.start_of(dt_emissao_w, 'mm', 0) = PKG_DATE_UTILS.start_of(clock_timestamp(), 'mm', 0)) then 
	CALL estornar_nota_fiscal(nr_seq_nf_p,nm_usuario_p);
end if;
if (PKG_DATE_UTILS.start_of(dt_emissao_w, 'mm', 0) < PKG_DATE_UTILS.start_of(clock_timestamp(), 'mm', 0)) then 
	CALL gerar_nf_credito(nr_seq_nf_p,nm_usuario_p);
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
