CREATE OR REPLACE PROCEDURE cargas_ger_atualizar_import ( ie_opcao_p text, -- I - Iniciar; F - Finalizar
 nr_sequencia_p ger_carga_arq.nr_sequencia%type, ds_nome_arquivo_p ger_carga_arq.ds_nome_arquivo%type default null) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then
    if (ie_opcao_p = 'I')then
        update  ger_carga_arq
        set     dt_inicio_importacao    = clock_timestamp(),
                ds_nome_arquivo         = ds_nome_arquivo_p
        where   nr_sequencia            = nr_sequencia_p;
    else
        update  ger_carga_arq
        set     dt_fim_importacao   = clock_timestamp()
        where   nr_sequencia        = nr_sequencia_p;
    end if;
    commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
