SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE carregar_301_just_medica ( nr_seq_dataset_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	d301_segmento_inv.nr_interno_segurado%type;
ds_quest_convenio_w	atend_prev_alta_just.ds_quest_convenio%type;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;


BEGIN

select	max(nr_interno_segurado)
into STRICT	cd_pessoa_fisica_w
from	d301_segmento_inv
where	nr_seq_dataset_ret	= nr_seq_dataset_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	ds_quest_convenio_w	:= substr(Obter_select_concatenado_bv('select ds_justificativa_med from d301_segmento_txt where nr_seq_dataset_ret = :nr_sequencia',
								'nr_sequencia='||nr_seq_dataset_p||';',
								' '),1,3999);
	select	max(a.nr_atendimento)
	into STRICT	nr_atendimento_w
	from	atendimento_paciente a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	coalesce(a.dt_alta::text, '') = '';

	if ((trim(both ds_quest_convenio_w) IS NOT NULL AND (trim(both ds_quest_convenio_w))::text <> '')) and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

		insert	into ATEND_PREV_ALTA_JUST(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_atend_prev_alta,
			ds_quest_convenio,
			nr_atendimento)
		values (nextval('atend_prev_alta_just_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null, --null pois o usuário irá selecionar quando acessar a justificativa
			ds_quest_convenio_w,
			nr_atendimento_w);

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carregar_301_just_medica ( nr_seq_dataset_p bigint, nm_usuario_p text) FROM PUBLIC;

