CREATE OR REPLACE PROCEDURE carregar_brasindice_lab ( cd_laboratorio_p text, ds_laboratorio_p text, nm_usuario_p text) AS $body$
DECLARE


ds_laboratorio_w	varchar(40);


BEGIN
if (cd_laboratorio_p IS NOT NULL AND cd_laboratorio_p::text <> '') and (ds_laboratorio_p IS NOT NULL AND ds_laboratorio_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	max(ds_laboratorio)
	into STRICT	ds_laboratorio_w
	from	brasindice_laboratorio
	where	cd_laboratorio = cd_laboratorio_p;

	if (coalesce(ds_laboratorio_w::text, '') = '') then
		insert
		into	brasindice_laboratorio(cd_laboratorio, ds_laboratorio, ie_situacao, dt_atualizacao, nm_usuario)
		values (cd_laboratorio_p, ds_laboratorio_p, 'A', clock_timestamp(), nm_usuario_p);
	elsif (ds_laboratorio_w <> ds_laboratorio_p) then
		update	brasindice_laboratorio
		set	ds_laboratorio = ds_laboratorio_p
		where	cd_laboratorio =  cd_laboratorio_p;
	end if;

	end;
end if;

end;
$body$
