CREATE OR REPLACE PROCEDURE carregar_brasindice_med ( cd_medicamento_p text, ds_medicamento_p text, nm_usuario_p text) AS $body$
DECLARE


ds_medicamento_w	varchar(80);


BEGIN
if (cd_medicamento_p IS NOT NULL AND cd_medicamento_p::text <> '') and (ds_medicamento_p IS NOT NULL AND ds_medicamento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	max(ds_medicamento)
	into STRICT	ds_medicamento_w
	from	brasindice_medicamento
	where	cd_medicamento = cd_medicamento_p;

	if (coalesce(ds_medicamento_w::text, '') = '') then
		insert
		into	brasindice_medicamento(cd_medicamento, ds_medicamento, ie_situacao, ie_solucoes, dt_atualizacao, nm_usuario)
		values (cd_medicamento_p, ds_medicamento_p, 'A', 'N', clock_timestamp(), nm_usuario_p);
	elsif (ds_medicamento_w <> ds_medicamento_p) then
		update	brasindice_medicamento
		set	ds_medicamento = ds_medicamento_p
		where	cd_medicamento =  cd_medicamento_p;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
