SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE carregar_mat_oc_nf_pendente ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_ordem_compra_w		bigint;
nr_item_oci_w			integer;
vl_unitario_item_oci_w		double precision;
qt_material_oci_w			double precision;
vl_unitario_item_nf_w		double precision;
qt_material_nf_w			double precision;
nr_item_nf_w			integer;
pr_dif_quantidade_w		double precision;
cd_estabelecimento_w		smallint;
pr_regra_w			double precision;
cd_unidade_medida_compra_w	varchar(30);
nr_seq_regra_w			bigint;

c01 CURSOR FOR
SELECT	n.nr_ordem_compra,
	e.nr_item_oci,
	i.vl_unitario_material,
	i.qt_material,
	i.cd_unidade_medida_compra
from	ordem_compra_item_entrega e,
	ordem_compra_item i,
	ordem_compra_nota_fiscal n
where	n.nr_sequencia		= nr_sequencia_p
and	n.nr_ordem_compra		= e.nr_ordem_compra
and	n.nr_item_oci		= e.nr_item_oci
and	i.nr_item_oci		= e.nr_item_oci
and	i.nr_ordem_compra		= e.nr_ordem_compra
and	coalesce(e.qt_prevista_entrega,0) > coalesce(e.qt_real_entrega,0)
and	exists (select	1
		from	regra_baixa_dif_oc_nf a
		where	i.cd_unidade_medida_compra 	= a.cd_unidade_medida_compra
		and	a.cd_estabelecimento		= cd_estabelecimento_w
		and	a.ie_situacao 			= 'A'
		and	a.pr_diferenca			<> 0);


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_ordem_compra_w,
	nr_item_oci_w,
	vl_unitario_item_oci_w,
	qt_material_oci_w,
	cd_unidade_medida_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	qt_item_nf,
		vl_unitario_item_nf,
		nr_item_nf
	into STRICT	qt_material_nf_w,
		vl_unitario_item_nf_w,
		nr_item_nf_w
	from	nota_fiscal_item
	where	nr_item_oci	= nr_item_oci_w
	and	nr_ordem_compra	= nr_ordem_compra_w
	and	nr_sequencia	= nr_sequencia_p;

	select	abs(obter_variacao_entre_valor(qt_material_oci_w, qt_material_nf_w))
	into STRICT	pr_dif_quantidade_w
	;

	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	regra_baixa_dif_oc_nf
	where	cd_unidade_medida_compra 	= cd_unidade_medida_compra_w
	and	cd_estabelecimento		= cd_estabelecimento_w
	and	ie_situacao 			= 'A'
	and	pr_diferenca			<> 0;

	if (nr_seq_regra_w > 0) then
		select	pr_diferenca
		into STRICT	pr_regra_w
		from	regra_baixa_dif_oc_nf
		where	nr_sequencia	= nr_seq_regra_w;

		if (pr_regra_w > 0) and (pr_regra_w >= abs(obter_variacao_entre_valor(qt_material_oci_w, qt_material_nf_w))) then
			begin

			insert into consist_baixa_dif_oc_nf(
					nr_sequencia,
					nm_usuario_nrec,
					nm_usuario,
					dt_atualizacao_nrec,
					dt_atualizacao,
					nr_seq_nf,
					nr_ordem_compra,
					nr_item_oci,
					nr_item_nf,
					qt_item_nf,
					qt_item_oci,
					pr_dif_quantidade,
					ie_baixar)
				values (	nextval('consist_baixa_dif_oc_nf_seq'),
					nm_usuario_p,
					nm_usuario_p,
					clock_timestamp(),
					clock_timestamp(),
					nr_sequencia_p,
					nr_ordem_compra_w,
					nr_item_oci_w,
					nr_item_nf_w,
					qt_material_nf_w,
					qt_material_oci_w,
					pr_dif_quantidade_w,
					'N');
			end;
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE carregar_mat_oc_nf_pendente ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

