CREATE OR REPLACE PROCEDURE carregar_pat_inventario_bem ( cd_centro_custo_p bigint, nr_seq_local_p bigint, cd_bem_p text, nr_seq_bem_p bigint, nr_seq_inventario_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estab_inventario_w			estabelecimento.cd_estabelecimento%type;
cd_empresa_inventario_w			empresa.cd_empresa%type;
cd_centro_custo_w			centro_custo.cd_centro_custo%type;
nr_seq_bem_w				pat_bem.nr_sequencia%type;
dt_consistencia_w			pat_inventario.dt_consistencia%type;


BEGIN

cd_centro_custo_w := cd_centro_custo_p;

select	a.cd_estabelecimento,
	obter_empresa_estab(a.cd_estabelecimento),
	a.dt_consistencia
into STRICT	cd_estab_inventario_w,
	cd_empresa_inventario_w,
	dt_consistencia_w
from	pat_inventario a
where	a.nr_sequencia = nr_seq_inventario_p;

if (coalesce(cd_centro_custo_w::text, '') = '') and (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') then
	select	cd_centro_custo
	into STRICT	cd_centro_custo_w
	from	pat_local
	where	nr_sequencia = nr_seq_local_p;
end if;

nr_seq_bem_w := obter_seq_bem(nr_seq_bem_p, cd_bem_p, cd_estab_inventario_w,  cd_empresa_inventario_w);

insert into pat_inventario_bem(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_inventario,
	cd_bem,
	nr_seq_local,
	cd_centro_custo,
	ds_consistencia,
	ie_barras,
	nr_seq_bem)
values (nextval('pat_inventario_bem_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_inventario_p,
	cd_bem_p,
	nr_seq_local_p,
	cd_centro_custo_w,
	null,
	'S',
	nr_seq_bem_w);

if (dt_consistencia_w IS NOT NULL AND dt_consistencia_w::text <> '') then
	begin
	update	pat_inventario a
	set	a.dt_consistencia  = NULL
	where	a.nr_sequencia = nr_seq_inventario_p;
	end;
end if;

commit;

end;
$body$
