CREATE OR REPLACE PROCEDURE carregar_w_pls_cnae ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_cnae_w			varchar(10);
ds_cnae_w			varchar(255);
qt_registro_w			integer	:= 0;

C01 CURSOR FOR
	SELECT	replace(replace(cd_cnae,'/',''),'-',''),
		ds_cnae
	from	w_pls_cnae;


BEGIN

open C01;
loop
fetch C01 into
	cd_cnae_w,
	ds_cnae_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_cnae
	where	cd_cnae	= cd_cnae_w;

	if (qt_registro_w	= 0) then
		insert into pls_cnae(nr_sequencia, ie_situacao, cd_estabelecimento,
			cd_cnae, ds_cnae, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec)
		values (	nextval('pls_cnae_seq'), 'A', cd_estabelecimento_p,
			cd_cnae_w, ds_cnae_w, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p);
	end if;
	end;
end loop;
close C01;

commit;

end;
