CREATE OR REPLACE PROCEDURE carrega_itens_cartao ( cd_material_p bigint, qt_material_p bigint, nr_seq_lote_fornec_p bigint, nr_seq_cartao_p bigint, cd_estabelecimento_p bigint, nm_usuario_retirada_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_consignado_w		varchar(1);
cd_unidade_medida_w	varchar(30);
cd_cgc_fornec_w		varchar(14);


BEGIN
select	substr(obter_dados_material(cd_material_p,'UMC'),1,30)                    
into STRICT	cd_unidade_medida_w                                                        
;

select	coalesce(max(ie_consignado),'0')                                                   
into STRICT	ie_consignado_w                                                            
from	material                                                                   
where	cd_material = cd_material_p;


if (ie_consignado_w <> '0') then                                                
	begin                                                                          
	if (coalesce(nr_seq_lote_fornec_p, 0) > 0) then                                     
		select	max(cd_cgc_fornec)
		into STRICT	cd_cgc_fornec_w                                                          
		from	material_lote_fornec                                                     
		where	nr_sequencia = nr_seq_lote_fornec_p;
	else                                                                           
		cd_cgc_fornec_w	:= obter_fornecedor_regra_consig(cd_estabelecimento_p, cd_material_p, '1');
	end if;
	end;
end if;

if (qt_material_p > 0) then                                                     

	select	nextval('sup_lanc_cartao_item_seq')                                        
	into STRICT	nr_sequencia_w                                                            
	;

	insert	into sup_lanc_cartao_item(                                              
			nr_sequencia,                                                                
			dt_atualizacao,                                                              
			nm_usuario,                                                                  
			dt_atualizacao_nrec,                                                         
			nm_usuario_nrec,                                                             
			nr_seq_lancamento,                                                           
			cd_material,                                                                 
			qt_material,                                                                 
			cd_unidade_medida,                                                           
			nr_seq_lote_fornec,
			nm_usuario_receb,
			ds_observacao)                                                               
	values (	nr_sequencia_w,                                                       
			clock_timestamp(),                                                                     
			nm_usuario_p,                                                                
			clock_timestamp(),                                                                     
			'N',                                                                         
			nr_Seq_cartao_p,                                                             
			cd_material_p,                                                               
			qt_material_p,                                                               
			cd_unidade_medida_w,                                                         
			CASE WHEN nr_seq_lote_fornec_p=0 THEN null  ELSE nr_seq_lote_fornec_p END ,
			nm_usuario_retirada_p,
			null);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
