CREATE OR REPLACE PROCEDURE carrega_macros_comunic_compras () AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_macro_w		varchar(4000);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_envio_comunic_evento
	where	coalesce(ds_macros_disponiveis::text, '') = '';



BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	obter_macro_comunic_compra(	nr_sequencia_w,	0, 0)
	into STRICT	ds_macro_w
	;

	update	regra_envio_comunic_evento
	set	ds_macros_disponiveis = ds_macro_w
	where	nr_sequencia = nr_sequencia_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
