CREATE OR REPLACE PROCEDURE carrega_mat_conv (cd_convenio_p text, -- vendo do dbpanel
 cd_mat_con_p text, nm_usuario_p text, --padrao
 cd_material_p text, ds_mat_con_p text, cd_grupo_p text, tx_conv_qt_p text, cd_unid_co_p text, cd_setor_a_p text, cd_cgc_p text, nr_seq_marca_p text, ie_tipo_atend_p text,  --vem do filtro
 dt_ini_vig_p text, cd_categoria_p text, cd_estabelecimento_p text ) AS $body$
DECLARE


qt_categoria_w 	bigint;
qt_registros_w	bigint;
qt_material_w	bigint;
qt_marca_w		bigint;
nr_seq_mar_w	bigint;


BEGIN

select  count(*)
into STRICT	qt_categoria_w
from	categoria_convenio
where	cd_convenio = cd_convenio_p
and		cd_categoria = cd_categoria_p;

select	count(*)
into STRICT	qt_registros_w
from 	conversao_material_convenio
where  	cd_convenio = cd_convenio_p
and  	cd_material_convenio = cd_mat_con_p
and  	cd_material          = cd_material_p
and 	ds_material_convenio  = ds_mat_con_p
and 	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria_p,0) = 0));

select 	count(*)
into STRICT 	qt_material_w
from 	material
where	cd_material = cd_material_p;

select 	count(*)
into STRICT	qt_marca_w
from 	marca
where 	nr_sequencia = nr_seq_marca_p;

if (qt_marca_w <> 0) then
	nr_seq_mar_w := (nr_seq_marca_p)::numeric;
else
	nr_seq_mar_w := null;
end if;

if (qt_material_w <> 0) and (qt_registros_w  = 0) and (cd_mat_con_p <> '') and (cd_categoria_p <> '') then
 begin
	insert into conversao_material_convenio(nr_sequencia,
											cd_convenio,
											cd_material_convenio,
											dt_atualizacao,
											nm_usuario,
											cd_grupo_material,
											cd_subgrupo_material,
											cd_classe_material,
											cd_material,
											ds_material_convenio,
											cd_grupo,
											tx_conversao_qtde,
											cd_unidade_convenio,
											cd_setor_atendimento,
											cd_cgc,
											ie_situacao,
											nr_seq_marca,
											ie_tipo_atendimento,
											dt_inicio_vigencia,
											cd_categoria,
											cd_estabelecimento)
								    values ( nextval('conversao_mat_convenio_seq'),
											cd_convenio_p,
											cd_mat_con_p,
											clock_timestamp(),
											nm_usuario_p,
											null,
											null,
											null,
											cd_material_p,
											ds_mat_con_p,
											cd_grupo_p,
											tx_conv_qt_p,
											cd_unid_co_p,
											cd_setor_a_p,
											cd_cgc_p,
											'A',
											nr_seq_mar_w,
											ie_tipo_atend_p,
											dt_ini_vig_p,
											cd_categoria_p,
											cd_estabelecimento_p);
 end;
 end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
