CREATE OR REPLACE PROCEDURE case_create_new ( NM_USUARIO_P text ,NM_USUARIO_NREC_P text ,CD_PESSOA_FISICA_P bigint ,NR_SEQ_TIPO_EPISODIO_P bigint ,NR_SEQ_SUBTIPO_EPISODIO_P bigint ,IE_STATUS_P text ,NR_SEQ_EPISODIO_P INOUT bigint ,CD_ESTABELECIMENTO_P bigint default null) AS $body$
BEGIN

	IF (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '' AND NM_USUARIO_P IS NOT NULL AND NM_USUARIO_P::text <> '') then

		select  nextval('episodio_paciente_seq')
		into STRICT    NR_SEQ_EPISODIO_P
		;
		
		INSERT INTO EPISODIO_PACIENTE(
			 nr_sequencia
			,nr_episodio 
			,dt_atualizacao
			,nm_usuario			
			,dt_atualizacao_nrec
			,nm_usuario_nrec	
			,dt_episodio
			,cd_pessoa_fisica
			,nr_seq_tipo_episodio
			,nr_seq_subtipo_episodio
			,ie_status
			,cd_estabelecimento
			)
		VALUES (
			 NR_SEQ_EPISODIO_P
			,null 			
			,clock_timestamp()			
			,NM_USUARIO_P		
			,clock_timestamp()
			,NM_USUARIO_NREC_P
			,clock_timestamp()
			,CD_PESSOA_FISICA_P
			,NR_SEQ_TIPO_EPISODIO_P
			,NR_SEQ_SUBTIPO_EPISODIO_P
			,IE_STATUS_P
			,CD_ESTABELECIMENTO_P);

		commit;

  end if;	

end;
$body$
