CREATE OR REPLACE PROCEDURE ca_cancelar_atividade ( nm_usuario_p text, dt_prevista_p timestamp, nr_sequencia_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_atividade_w			bigint;

BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (dt_prevista_p IS NOT NULL AND dt_prevista_p::text <> '') then

	update	ca_controle_atividade
	set		dt_cancelamento			= clock_timestamp(),
			nm_usuario_cancelamento = nm_usuario_p,
			ie_status               = 'C'
	where	nr_sequencia = nr_sequencia_p;

	select	max(nr_seq_atividade)
	into STRICT	nr_seq_atividade_w
	from 	ca_controle_atividade
	where 	nr_sequencia = nr_sequencia_p;

	CALL ca_gerar_cont_atividade(nm_usuario_p,cd_estabelecimento_p,nr_seq_atividade_w,dt_prevista_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
