SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ca_executar_atividade ( nm_usuario_p text, nr_sequencia_p bigint) AS $body$
BEGIN
 
if (coalesce(nr_sequencia_p,0) > 0) then 
 
	update	ca_controle_atividade 
	set		dt_execucao = clock_timestamp(), 
			ie_status = 'R', 
			dt_atualizacao = clock_timestamp(), 
			nm_usuario = nm_usuario_p, 
			nm_usuario_exec = nm_usuario_p, 
			cd_pessoa_fisica = CASE WHEN cd_pessoa_fisica='' THEN Obter_Pf_Usuario(nm_usuario_p,'C')  ELSE cd_pessoa_fisica END  
	where	nr_sequencia = nr_sequencia_p;
	 
	CALL ca_mov_estoque_materiais(nr_sequencia_p,nm_usuario_p);
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
