CREATE OR REPLACE PROCEDURE ca_gerar_itens_contr_atividade ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE



nr_seq_atividade_w			bigint;
qt_dias_atividade_w			bigint;
qt_dias_geracao_atividade_w	bigint;
dt_execucao_w				timestamp;
dt_prevista_w				timestamp;
nr_seq_contr_atividade_w	bigint;
dt_geracao_w				timestamp;
dt_execucao_atividade_w		timestamp;

C01 CURSOR FOR
	SELECT	coalesce(nr_sequencia,0),
			coalesce(qt_dias,0),
			coalesce(qt_dias_geracao,0)
	from	ca_atividade
	where	ie_situacao = 'A';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_atividade_w,
	qt_dias_atividade_w,
	qt_dias_geracao_atividade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	dt_prevista_w := null;
	dt_geracao_w := null;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_contr_atividade_w
	from	ca_controle_atividade
	where	nr_seq_atividade = nr_seq_atividade_w;

	if (nr_seq_contr_atividade_w > 0) then

		select	dt_execucao
		into STRICT	dt_execucao_atividade_w
		from	ca_controle_atividade
		where	nr_sequencia = nr_seq_contr_atividade_w;

	end if;

	if (nr_seq_contr_atividade_w > 0) and (dt_execucao_atividade_w IS NOT NULL AND dt_execucao_atividade_w::text <> '') then

		dt_prevista_w := dt_execucao_atividade_w + qt_dias_atividade_w;
		dt_geracao_w := dt_prevista_w - qt_dias_geracao_atividade_w;

	elsif (nr_seq_contr_atividade_w = 0) and (coalesce(dt_execucao_atividade_w::text, '') = '') then

		dt_prevista_w := clock_timestamp();
		dt_geracao_w := clock_timestamp();

	end if;

	if	((dt_prevista_w IS NOT NULL AND dt_prevista_w::text <> '') and (dt_geracao_w IS NOT NULL AND dt_geracao_w::text <> '') and (trunc(dt_geracao_w) = trunc(clock_timestamp())))then

		CALL ca_gerar_cont_atividade(nm_usuario_p,cd_estabelecimento_p,nr_seq_atividade_w,dt_prevista_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
