SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ca_inserir_protocolos_exames ( nm_usuario_p text, ds_seq_protocolos_p text, nr_seq_analise_p bigint) AS $body$
DECLARE


nr_seq_exame_w		bigint;
cd_unidade_medida_w	varchar(30);
ds_protocolos_w		varchar(4000);
ds_codigo_temp_w 	varchar(4000);
ds_codigo_aux_w  	varchar(2);
k   				smallint;
i   				smallint;
nr_seq_protocolo_w	bigint;
qt_exame_analise_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_exame
	from	ca_protocolo_exame
	where	nr_seq_protocolo = nr_seq_protocolo_w;


BEGIN

if (ds_seq_protocolos_p IS NOT NULL AND ds_seq_protocolos_p::text <> '') then

	ds_protocolos_w := substr(ds_seq_protocolos_p,1,4000);

	/* O IF abaixo é apenas utilizado para adicionar uma vírgula no final,
	quando o método não retornar dessa forma (caso da utilização do WCheckList via Schematic)*/
	if (SUBSTR(ds_protocolos_w,LENGTH(ds_protocolos_w),LENGTH(ds_protocolos_w)) <> ',') then
		ds_protocolos_w	:= ds_protocolos_w || ',';
	end if;

	k := 1;
	for i in k..length(ds_protocolos_w) loop
		begin

		ds_codigo_aux_w := SUBSTR(ds_protocolos_w,i,1);

		if (ds_codigo_aux_w = ',') then

			nr_seq_protocolo_w := obter_somente_numero(ds_codigo_temp_w);

			open C01;
			loop
			fetch C01 into
				nr_seq_exame_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin

				select	count(*)
				into STRICT	qt_exame_analise_w
				from	ca_exame_realizado
				where	nr_seq_analise = nr_seq_analise_p
				and		nr_seq_exame = nr_seq_exame_w;

				if (qt_exame_analise_w = 0) then

					select 	cd_unidade_medida
					into STRICT	cd_unidade_medida_w
					from 	ca_exame
					where 	nr_sequencia = nr_seq_exame_w;

					insert into	ca_exame_realizado(	nr_seq_analise,
													nr_seq_exame,
													dt_atualizacao,
													nm_usuario,
													dt_atualizacao_nrec,
													nm_usuario_nrec,
													cd_unidade_medida)
											values (nr_seq_analise_p,
													nr_seq_exame_w,
													clock_timestamp(),
													nm_usuario_p,
													clock_timestamp(),
													nm_usuario_p,
													cd_unidade_medida_w);
				end if;
				end;
			end loop;
			close C01;



			ds_codigo_temp_w := '';
		else
			ds_codigo_temp_w := ds_codigo_temp_w||ds_codigo_aux_w;
		end if;
		end;
	end loop;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
