SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ca_liberar_resultado ( nm_usuario_p text, ie_opcao_p text, nr_sequencia_p bigint) AS $body$
DECLARE


/* ie_opcao_p
L - Liberar
D - Desfazer liberação
*/
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	if (ie_opcao_p = 'L') then

		update	hd_analise_agua
		set	dt_liberacao_result = clock_timestamp(),
			nm_usuario_lib_result = nm_usuario_p,
			ie_status = 'C'
		where 	nr_sequencia = nr_sequencia_p;

	elsif (ie_opcao_p = 'D') then

		update	hd_analise_agua
		set	dt_liberacao_result  = NULL,
			nm_usuario_lib_result  = NULL,
			ie_status = 'R'
		where 	nr_sequencia = nr_sequencia_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
