CREATE OR REPLACE PROCEDURE ca_registrar_manutencao ( nr_seq_reservatorio_p bigint, cd_pessoa_responsavel_p text, ds_motivos_manutencao_p text, ds_manutencao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_motivos_manutencao_w	varchar(4000);
ds_codigo_temp_w	varchar(4000);
ds_codigo_aux_w		varchar(2);

k			smallint;
i			smallint;


BEGIN

if (coalesce(ds_motivos_manutencao_p::text, '') = '') then

	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262337);

elsif (coalesce(nr_seq_reservatorio_p,0) > 0) and (cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '') then

	ds_motivos_manutencao_w := ds_motivos_manutencao_p;
	k := 1;
	for i in k..length(ds_motivos_manutencao_w) loop
		begin

		ds_codigo_aux_w	:= substr(ds_motivos_manutencao_w,i,1);

		if (ds_codigo_aux_w = ',') then

			insert into ca_manutencao_reservatorio(nr_sequencia,
				 dt_registro,
				 nr_seq_res_agua,
				 nr_seq_motivo_manutencao,
				 cd_pessoa_fisica,
				 ds_observacao,
				 dt_atualizacao,
				 dt_atualizacao_nrec,
				 nm_usuario,
				 nm_usuario_nrec)
			values (nextval('ca_manutencao_reservatorio_seq'),
				 clock_timestamp(),
				 nr_seq_reservatorio_p,
				 obter_somente_numero(ds_codigo_temp_w),
				 cd_pessoa_responsavel_p,
				 ds_manutencao_p,
				 clock_timestamp(),
				 clock_timestamp(),
				 nm_usuario_p,
				 nm_usuario_p);

			ds_codigo_temp_w := '';
		else
			ds_codigo_temp_w := ds_codigo_temp_w||ds_codigo_aux_w;
		end if;
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
