CREATE OR REPLACE PROCEDURE ca_registrar_osmose_manutencao ( nr_seq_osmose_p bigint, nr_seq_membrana_p bigint, ds_seq_motivo_manutencao_p text, cd_pessoa_responsavel_p text, ds_manutencao_p text, nm_usuario_p text ) AS $body$
DECLARE


ds_motivos_manutencao_w varchar(4000);
ds_codigo_temp_w varchar(4000);
ds_codigo_aux_w  varchar(2);

k   smallint;
i   smallint;


BEGIN

if (coalesce(ds_seq_motivo_manutencao_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262335);

elsif (coalesce(nr_seq_osmose_p,0) > 0) and (cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '') then

	ds_motivos_manutencao_w := ds_seq_motivo_manutencao_p;
	k := 1;
	for i in k..length(ds_motivos_manutencao_w) loop
		begin

		ds_codigo_aux_w := substr(ds_motivos_manutencao_w,i,1);

		if (ds_codigo_aux_w = ',') then

			insert into 	ca_membrana_manutencao(nr_sequencia,
				dt_instalacao,
				nr_seq_osmose,
				nr_seq_membrana,
				nr_seq_motivo_troca,
				cd_pessoa_fisica,
				ds_observacao,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec)
			values (nextval('ca_membrana_manutencao_seq'),
				clock_timestamp(),
				CASE WHEN nr_seq_osmose_p=0 THEN null  ELSE nr_seq_osmose_p END ,
				CASE WHEN nr_seq_membrana_p=0 THEN null  ELSE nr_seq_membrana_p END ,
				obter_somente_numero(ds_codigo_temp_w),
				cd_pessoa_responsavel_p,
				ds_manutencao_p,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p);

			ds_codigo_temp_w := '';
		else
			ds_codigo_temp_w := ds_codigo_temp_w||ds_codigo_aux_w;
		end if;
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
