SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cbhpm_carga_estrutura ( cd_capitulo_p bigint, cd_espec_p bigint, cd_grupo_p bigint, cd_especialidade_p text, cd_grupo_proc_p text, ds_capitulo_p text, ds_especialidade_p text, ds_grupo_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_registros_w	bigint;
ds_erro_w		varchar(2000);
cd_capitulo_w		integer;
ds_capitulo_w		varchar(255);


BEGIN

cd_capitulo_w		:= cd_capitulo_p + 10;
ds_capitulo_w		:= substr(InitCap(ds_capitulo_p),1,40);
qt_registros_w	:= 0;
ds_erro_w		:= WHEB_MENSAGEM_PCK.get_texto(281123);

/* Carga da area de procedimentos */

begin
select count(*)
into STRICT	 qt_registros_w
from	 area_procedimento
where	 cd_area_procedimento	= cd_capitulo_w;
if (qt_registros_w	= 0) then
	 begin
	 insert into area_procedimento(
			 CD_AREA_PROCEDIMENTO ,
			 DS_AREA_PROCEDIMENTO ,
			 DT_ATUALIZACAO       ,
			 NM_USUARIO           ,
			 IE_ORIGEM_PROCED     ,
			 CD_ORIGINAL)
	 values (
			 cd_capitulo_p + 10,
			 ds_capitulo_w,
			 clock_timestamp(),
			 'Tasy',
			 5,
			 cd_capitulo_w);
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
	end;
end if;
end;


/* Carga da especialidade de procedimentos */

begin
select count(*)
into STRICT	 qt_registros_w
from	 especialidade_proc
where	 cd_especialidade	= cd_espec_p;
if (qt_registros_w	= 0) then
	 begin
	 insert into especialidade_proc(
			 CD_ESPECIALIDADE              ,
			 DS_ESPECIALIDADE              ,
			 DT_ATUALIZACAO                ,
			 NM_USUARIO                    ,
			 CD_AREA_PROCEDIMENTO          ,
			 CD_ESPECIALIDADE_MEDICA       ,
			 IE_ORIGEM_PROCED              ,
			 CD_ORIGINAL                   )
	 values (
			 cd_espec_p,
			 InitCap(substr(ds_especialidade_p,1,240)),
			 clock_timestamp(),
			 'Tasy',
			 cd_capitulo_w,
			 null,
			 5,
			 somente_numero(cd_especialidade_p));
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
	end;
end if;
end;

/* Carga da grupo de procedimentos */

begin
select count(*)
into STRICT	 qt_registros_w
from	 grupo_proc
where	 cd_grupo_proc	= cd_grupo_p;
if (qt_registros_w	= 0) then
	 begin
	 insert into grupo_proc(
			 CD_GRUPO_PROC          ,
			 DS_GRUPO_PROC          ,
			 CD_ESPECIALIDADE       ,
			 IE_SITUACAO            ,
			 DT_ATUALIZACAO         ,
			 NM_USUARIO             ,
			 IE_ORIGEM_PROCED       ,
			 CD_ORIGINAL            )
	 values (
			 cd_grupo_p,
			 InitCap(substr(ds_grupo_p,1,240)),
			 cd_espec_p,
			 'A',
			 clock_timestamp(),
			 'Tasy',
			 5,
			 somente_numero(cd_grupo_proc_p));
	exception
  	when others then
    		ds_erro_w	:= SQLERRM(SQLSTATE);
	end;
end if;
end;

commit;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
