CREATE OR REPLACE PROCEDURE cf_atualiza_protocolo_xml ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_interno_conta_w	conta_paciente.nr_interno_conta%type;
ie_atualizou_w		varchar(1) 	:= '';

c01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo = nr_seq_protocolo_p;


BEGIN 
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	 
	update protocolo_convenio 
	set   ie_status_protocolo = 2, 
		nm_usuario = nm_usuario_p 
	where  nr_seq_protocolo = nr_seq_protocolo_p;
	 
end if;
 
open c01;
loop 
fetch c01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ie_atualizou_w := cf_define_proximo_status(nr_interno_conta_w, ie_atualizou_w, nm_usuario_p);
	 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
