SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cf_cancelar_protocolo (nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
nr_Seq_regra_fluxo_atual_w	conta_paciente.nr_seq_regra_fluxo%type;
nr_atendimento_w			conta_paciente.nr_atendimento%type;
dt_fim_conta_w				atendimento_paciente.dt_fim_conta%type;
ie_pendencia_w				cf_status_faturamento.ie_pendencia%type;
ie_perdido_w				cf_status_faturamento.ie_perda%type;
nr_seq_regra_fluxo_w		cf_regra_estagio.nr_seq_regra%type;
nr_seq_status_fat_w			cf_regra_estagio.nr_seq_status_fat%type;
ie_status_atual_w			varchar(1);
nr_seq_status_fat_atual_w	conta_paciente.nr_seq_status_fat%type;
nr_interno_conta_w			conta_paciente.nr_interno_conta%type;
qt_prot_def_w				bigint;
qt_conta_prot_w				bigint;

C01 CURSOR FOR 
	SELECT		b.nr_sequencia, 
				b.nr_seq_status_fat 
	from		cf_regra_estagio b 
	where		b.nr_seq_regra = nr_Seq_regra_fluxo_atual_w 
	and			coalesce(ie_pendencia_w::text, '') = '' 
	and			ie_perdido_w = 'N' 
	order by 	b.nr_ordem desc;
	
C02 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo = nr_seq_protocolo_p 
	and 	qt_prot_def_w  = 0 
	order by nr_interno_conta;


BEGIN 
 
select 	count(*) 
into STRICT	qt_prot_def_w 
from 	protocolo_convenio 
where 	nr_seq_protocolo = nr_seq_protocolo_p 
and		ie_status_protocolo = 2;
 
open C02;
loop 
fetch C02 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		ie_status_atual_w:= 'N';
		select	max(a.nr_seq_status_fat), 
				max(a.nr_seq_regra_fluxo) 
		into STRICT	nr_seq_status_fat_atual_w, 
				nr_Seq_regra_fluxo_atual_w 
		from	conta_paciente a 
		where 	nr_interno_conta = nr_interno_conta_w;
		 
		select	max(ie_pendencia), 
				coalesce(max(ie_perda),'N') 
		into STRICT	ie_pendencia_w, 
				ie_perdido_w 
		from	cf_status_faturamento 
		where	nr_sequencia = nr_seq_status_fat_atual_w;
 
		-- ESTAGIO ANTERIOR.... 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_regra_fluxo_w, 
			nr_seq_status_fat_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			 
			begin 
			 
			if (ie_status_atual_w = 'N') then 
					 
					if (nr_seq_status_fat_w = nr_seq_status_fat_atual_w) then 
							ie_status_atual_w := 'S';
					else 
							ie_status_atual_w := 'N';
					end if;
					 
			else 
					update	conta_paciente 
					set		nr_seq_status_fat = nr_seq_status_fat_w, 
							nm_usuario = nm_usuario_p 
					where	nr_interno_conta = nr_interno_conta_w;
							 
					delete	from w_cf_consulta_guia 
					where	nm_usuario = nm_usuario_p 
					and		nr_interno_conta = nr_interno_conta_w;
						 
					exit;
			 
			end if;
			 
			end;
			 
		end loop;
		close C01;	
		 
		select 	max(nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from 	conta_paciente 
		where 	nr_interno_conta = nr_interno_conta_w;
 
		select 	max(dt_fim_conta) 
		into STRICT	dt_fim_conta_w 
		from 	atendimento_paciente 
		where 	nr_atendimento = nr_atendimento_w;
 
		if (dt_fim_conta_w IS NOT NULL AND dt_fim_conta_w::text <> '') then 
			CALL Abrir_Atendimento(nr_atendimento_w, nm_usuario_p);
		end if;
		 
		-- Tirar a conta do protocolo 
		update	conta_paciente 
		set		nr_seq_protocolo	 = NULL, 
				nr_protocolo		= 0, 
				nm_usuario			= nm_usuario_p, 
				dt_atualizacao		= clock_timestamp() 
		where	nr_interno_conta	= nr_interno_conta_w;
 
		-- Mudar Status da conta para provisório 
		Update 	conta_paciente 
		set 	ie_status_acerto	= 1, 
				dt_atualizacao		= clock_timestamp(), 
				dt_geracao_tiss		 = NULL, 
				nm_usuario			= nm_usuario_p 
		where	nr_interno_conta	= nr_interno_conta_w;
			 
		end;
end loop;
close C02;
 
select 	count(*) 
into STRICT	qt_conta_prot_w 
from 	conta_paciente 
where 	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (qt_conta_prot_w = 0) then -- Todas as contas foram retiradas do protocolo 
 
		delete 	 
		from 	protocolo_convenio 
		where 	nr_seq_protocolo = nr_seq_protocolo_p;
 
end if;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cf_cancelar_protocolo (nr_seq_protocolo_p bigint, nm_usuario_p text) FROM PUBLIC;

