CREATE OR REPLACE PROCEDURE cf_definir_perdido (nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Alterar o status faturamento para "perdido" 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
nr_seq_status_fat_w		conta_paciente.nr_seq_status_fat%type;
ie_pendencia_w			cf_status_faturamento.ie_pendencia%type;
ds_status_fat_w			cf_status_faturamento.ds_status_fat%type;
nr_seq_perda_w	 		cf_status_faturamento.nr_sequencia%type;


BEGIN 
 
select	max(a.nr_seq_status_fat), 
		max(b.ie_pendencia), 
		max(b.ds_status_fat) 
into STRICT	nr_seq_status_fat_w, 
		ie_pendencia_w, 
		ds_status_fat_w 
from	conta_paciente a, 
		cf_status_faturamento b 
where	a.nr_interno_conta = nr_interno_conta_p 
and		a.nr_seq_status_fat = b.nr_sequencia;
 
if (ie_pendencia_w IS NOT NULL AND ie_pendencia_w::text <> '') then 
		select	coalesce(max(nr_sequencia), 0) 
		into STRICT	nr_seq_perda_w 
		from	cf_status_faturamento 
		where	ie_perda = 'S' 
		and		ie_situacao = 'A';
		 
		update	conta_paciente 
		set		nr_seq_status_fat = CASE WHEN nr_seq_perda_w=0 THEN nr_seq_status_fat  ELSE nr_seq_perda_w END , 
				nm_usuario = nm_usuario_p 
		where	nr_interno_conta = nr_interno_conta_p;
		 
		delete	 
		from	w_cf_consulta_guia 
		where	nr_interno_conta = nr_interno_conta_p 
		and		nm_usuario = nm_usuario_p;
		 
else 
		update	w_cf_consulta_guia 
		set		ds_status_fat = ds_status_fat_w, 
				nr_seq_status_fat = nr_seq_status_fat_w 
		where	nr_interno_conta = nr_interno_conta_p;
 
end if;
 
 
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
