CREATE OR REPLACE PROCEDURE cf_despachar_remessa ( nr_remessa_p protocolo_documento.nr_sequencia%type, nm_usuario_p protocolo_documento.nm_usuario_envio%type, ie_destino_remessa_p protocolo_documento.ie_destino_remessa%type ) AS $body$
DECLARE

 
nr_seq_status_fat_w	conta_paciente.nr_seq_status_fat%type;
nr_seq_status_mob_w	conta_paciente.nr_seq_status_mob%type;
ie_status_protocolo_w	protocolo_documento.ie_status_protocolo%type;
nr_interno_conta_w	protocolo_doc_item.nr_seq_interno%type;
nm_usuario_receb_w	protocolo_documento.nm_usuario_receb%type;
cd_pessoa_destino_w	protocolo_documento.cd_pessoa_destino%type;
ie_define_responsavel_w	cf_regra_geracao_remessa.ie_define_responsavel%type;
dt_rec_destino_w	timestamp;

c01 CURSOR FOR 
	SELECT	nr_seq_interno 
	from	protocolo_doc_item 
	where	nr_sequencia = nr_remessa_p;


BEGIN 
 
ie_status_protocolo_w	:= 'E';
nm_usuario_receb_w 	:= null;
dt_rec_destino_w	:= null;
ie_define_responsavel_w	:= null;
cd_pessoa_destino_w	:= null;
 
 
if (nr_remessa_p IS NOT NULL AND nr_remessa_p::text <> '') then 
	begin 
 
	select	max(ie_define_responsavel) 
	into STRICT	ie_define_responsavel_w 
	from	cf_regra_geracao_remessa 
	where	ie_destino_remessa = ie_destino_remessa_p;
 
	if (ie_define_responsavel_w = 'S') then 
		select	max(cd_pessoa_destino) 
		into STRICT	cd_pessoa_destino_w 
		from	protocolo_documento 
		where	nr_sequencia = nr_remessa_p;
	end if;
		 
	if (cd_pessoa_destino_w IS NOT NULL AND cd_pessoa_destino_w::text <> '') then 
		begin 
		ie_status_protocolo_w := 'R';
		dt_rec_destino_w := clock_timestamp();
 
		select	max(nm_usuario) 
		into STRICT	nm_usuario_receb_w 
		from	usuario 
		where	cd_pessoa_fisica = cd_pessoa_destino_w;
		end;
	end if;
 
	open C01;
	loop 
	fetch C01 into	 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
 
		select	coalesce(max(nr_seq_status_fat),0) 
		into STRICT	nr_seq_status_fat_w 
		from	conta_paciente 
		where	nr_interno_conta = nr_interno_conta_w;
 
		if (ie_destino_remessa_p = 'E') then -- Envio 
			nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'G');
		elsif (ie_destino_remessa_p = 'F') then -- Faturamento 
			nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'E');
		elsif (ie_destino_remessa_p = 'I') then -- Faturista 
			nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'F');
		elsif (ie_destino_remessa_p = 'R') then -- Recepção 
			nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'D');
		elsif (ie_destino_remessa_p = 'U') then -- Unidade 
			nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'H');
		end if;
 
		update	conta_paciente 
		set		nr_seq_status_mob = coalesce(nr_seq_status_mob_w, nr_seq_status_mob), 
				nm_usuario = nm_usuario_p 
		where	nr_interno_conta = nr_interno_conta_w;
 
		end;
	end loop;
	close C01;
 
	update	protocolo_documento 
	set	nm_usuario_envio = nm_usuario_p, 
		dt_envio = clock_timestamp(), 
		ie_status_protocolo = ie_status_protocolo_w, 
		nm_usuario_receb = nm_usuario_receb_w, 
		dt_rec_destino = dt_rec_destino_w 
	where	nr_sequencia = nr_remessa_p;
 
	commit;
 
	end;
 
end if;
 
end;
$body$
