CREATE OR REPLACE PROCEDURE cf_gerar_protocolo ( cd_convenio_p bigint, nr_protocolo_p text, ie_tipo_protocolo_p bigint, cd_estabelecimento_p bigint, dt_entrega_convenio_p timestamp, nr_seq_protocolo_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
dt_mesano_referencia_w				timestamp;
nr_seq_protocolo_w				bigint;

 

BEGIN 
 
select		obter_data_ref_protocolo(cd_convenio_p, ie_tipo_protocolo_p), 
		nextval('protocolo_convenio_seq') 
into STRICT		dt_mesano_referencia_w, 
		nr_seq_protocolo_w
;
 
insert into protocolo_convenio(	cd_convenio, 
					nr_protocolo, 
					ie_status_protocolo, 
					dt_periodo_inicial, 
					dt_periodo_final, 
					dt_atualizacao, 
					nm_usuario, 
					ie_tipo_protocolo, 
					nr_seq_protocolo, 
					dt_mesano_referencia, 
					cd_estabelecimento, 
					nr_seq_lote_receita, 
					nr_seq_lote_repasse, 
					nr_seq_lote_grat, 
					dt_entrega_convenio) 
values (	cd_convenio_p, 
					nr_protocolo_p, 
					1, 
					trunc(clock_timestamp(), 'dd'), 
					fim_dia(clock_timestamp()), 
					clock_timestamp(), 
					nm_usuario_p, 
					ie_tipo_protocolo_p, 
					nr_seq_protocolo_w, 
					dt_mesano_referencia_w, 
					cd_estabelecimento_p, 
					0, 
					0, 
					0, 
					dt_entrega_convenio_p);
									 
if (coalesce(obter_valor_param_usuario(3013, 6, obter_perfil_ativo, nm_usuario_p, 0), 'N') = 'S') then	 
	 
	CALL cf_inserir_protocolo_lote(nr_seq_protocolo_w,nm_usuario_p);
 
end if;								
									 
nr_seq_protocolo_p := nr_seq_protocolo_w;
 
commit;
end;
