CREATE OR REPLACE PROCEDURE cf_gerar_remessa_item ( nr_remessa_p bigint, nr_seq_item_p bigint, nm_usuario_p text, nr_seq_interno_p bigint, nr_guia_p text, nr_atendimento_p bigint, ie_destino_remessa_p text ) AS $body$
DECLARE

 
nr_seq_status_fat_w	conta_paciente.nr_seq_status_fat%type;
nr_seq_status_mob_w	conta_paciente.nr_seq_status_mob%type;


BEGIN 
 
select	max(nr_seq_status_fat) 
into STRICT	nr_seq_status_fat_w 
from	conta_paciente 
where	nr_interno_conta = nr_seq_interno_p;
 
if (ie_destino_remessa_p = 'E') then -- Envio 
	nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'O');
elsif (ie_destino_remessa_p = 'F') then -- Faturamento 
	nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'N');
elsif (ie_destino_remessa_p = 'I') then -- Faturista 
	nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'M');
elsif (ie_destino_remessa_p = 'R') then -- Recepção 
	nr_seq_status_mob_w := cf_obter_status_mob(nr_seq_status_fat_w, 'L');
end if;
 
insert into	protocolo_doc_item(nr_sequencia, 
		 nr_seq_item, 
		 dt_atualizacao, 
		 nm_usuario, 
		 nr_guia, 
		 nr_documento, 
		 nr_seq_interno) 
	values (nr_remessa_p, 
		 nr_seq_item_p, 
		 clock_timestamp(), 
		 nm_usuario_p, 
		 nr_guia_p, 
		 nr_atendimento_p, 
		 nr_seq_interno_p);
 
update	conta_paciente 
set		nr_seq_status_mob = coalesce(nr_seq_status_mob_w, nr_seq_status_mob), 
		nm_usuario = nm_usuario_p 
where	nr_interno_conta = nr_seq_interno_p;
 
commit;
 
end;
$body$
