SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE chamar_paciente_pc ( nr_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

 
/* 
ie_opcao_p: 
CE	= Chamar Paciente Acolhimento (enfermagem) 
CC	= Chamar Paciente Consulta 
C	= Cancelar Chamada Paciente 
*/
 
 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	if (ie_opcao_p = 'CE') then 
		begin 
		update	atendimento_paciente 
		set	dt_chamada_enfermagem = clock_timestamp(), 
			nm_usuario = nm_usuario_p 
		where	nr_atendimento = nr_atendimento_p;
		end;
	elsif (ie_opcao_p = 'CC') then 
		begin 
		update	atendimento_paciente 
		set	dt_chamada_paciente = clock_timestamp(), 
			nm_usuario = nm_usuario_p 
		where	nr_atendimento = nr_atendimento_p;
		end;
	elsif (ie_opcao_p = 'C') then 
		begin 
		update	atendimento_paciente 
		set	dt_chamada_enfermagem  = NULL, 
			dt_chamada_paciente  = NULL, 
			nm_usuario = nm_usuario_p 
		where	nr_atendimento = nr_atendimento_p;
		end;
	end if;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
