CREATE OR REPLACE PROCEDURE chamar_paciente_resp_pa ( nr_atendimentos_p text, nr_seq_local_p bigint, cd_medico_p text, qt_hora_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_atendimentos_w	varchar(2000);
nr_atendimento_w	bigint;
nr_pos_virgula_w	bigint;
		

BEGIN 
 
if (nr_atendimentos_p IS NOT NULL AND nr_atendimentos_p::text <> '') and (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (qt_hora_p IS NOT NULL AND qt_hora_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin	 
	ds_lista_atendimentos_w := nr_atendimentos_p;
	 
	while(ds_lista_atendimentos_w IS NOT NULL AND ds_lista_atendimentos_w::text <> '') loop 
		begin 
		nr_pos_virgula_w := position(',' in ds_lista_atendimentos_w);
		 
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_atendimento_w 	:= (substr(ds_lista_atendimentos_w,1,nr_pos_virgula_w-1))::numeric;
			ds_lista_atendimentos_w	:= substr(ds_lista_atendimentos_w,nr_pos_virgula_w+1,length(ds_lista_atendimentos_w));
			 
			if (nr_atendimento_w > 0) then 
				begin 
				CALL chamada_paciente_pa(nr_atendimento_w,nr_seq_local_p,cd_medico_p,'CP',qt_hora_p,nm_usuario_p);
				end;
			end if;
			end;
		else 
			begin 
			ds_lista_atendimentos_w := null;
			end;
		end if;
		end;
	end loop;	
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
