CREATE OR REPLACE PROCEDURE change_consultation_purpose (nr_atendimento_p atendimento_paciente_aux.nr_atendimento%TYPE, ie_finalidade_consulta_p atendimento_paciente_aux.ie_finalidade_consulta%TYPE, ie_causa_externa_p atendimento_paciente_aux.ie_causa_externa%TYPE, nm_usuario_p atendimento_paciente_aux.nm_usuario%TYPE ) AS $body$
DECLARE


dt_atual_s      CONSTANT timestamp := clock_timestamp();
nr_sequencia_p  atendimento_paciente_aux.nr_sequencia%TYPE;

BEGIN

SELECT  MAX(atendimento_paciente_aux.nr_sequencia)
INTO STRICT    nr_sequencia_p
FROM    atendimento_paciente_aux
WHERE   nr_atendimento = nr_atendimento_p;

IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') THEN
    UPDATE atendimento_paciente_aux
    SET ie_finalidade_consulta    = ie_finalidade_consulta_p,
        ie_causa_externa          = ie_causa_externa_p,
        nm_usuario                = nm_usuario_p,
        nm_usuario_nrec           = nm_usuario_p,
        dt_atualizacao            = dt_atual_s,
        dt_atualizacao_nrec       = dt_atual_s
    WHERE nr_atendimento          = nr_atendimento_p;
ELSE
    INSERT INTO atendimento_paciente_aux(
        nr_sequencia,
        dt_atualizacao,
        dt_atualizacao_nrec,
        nm_usuario,
        nm_usuario_nrec,
        nr_atendimento,
        ie_finalidade_consulta,
        ie_causa_externa
    ) VALUES (
        nextval('atendimento_paciente_aux_seq'),
        dt_atual_s,
        dt_atual_s,
        nm_usuario_p,
        nm_usuario_p,
        nr_atendimento_p,
        ie_finalidade_consulta_p,
        ie_causa_externa_p);
END IF;

COMMIT;
END;
$body$
