CREATE OR REPLACE PROCEDURE change_date_external_physician ( cd_medico_p pf_medico_externo.cd_medico%type) AS $body$
DECLARE


  item RECORD;

BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	
	for item in (	SELECT  pme.nr_sequencia
			from    pf_medico_externo pme
			where   ((pme.dt_inicio_vigencia <= clock_timestamp() or coalesce(pme.dt_inicio_vigencia::text, '') = '') and (  coalesce(pme.dt_fim_vigencia::text, '') = '' or pme.dt_fim_vigencia >= clock_timestamp()))
			and     pme.cd_medico = cd_medico_p) loop
			
		update	pf_medico_externo
		set	dt_fim_vigencia = clock_timestamp()
		where	nr_sequencia = item.nr_sequencia;
		
	end loop;
	
	commit;
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
