CREATE OR REPLACE PROCEDURE change_dt_episode ( dt_entrada_p atendimento_paciente.dt_entrada%type, nr_seq_episodio_p atendimento_paciente.nr_seq_episodio%type) AS $body$
DECLARE

			
dt_episodio_w	episodio_paciente.dt_episodio%type;


BEGIN

if (nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') then
	select	max(ep.dt_episodio)
	into STRICT	dt_episodio_w
	from 	episodio_paciente ep
	where 	ep.nr_sequencia = nr_seq_episodio_p;
	
	if ((dt_episodio_w IS NOT NULL AND dt_episodio_w::text <> '') and dt_entrada_p < dt_episodio_w) then
		update	episodio_paciente ep
		set		ep.dt_episodio = dt_entrada_p,
				dt_atualizacao = clock_timestamp(),     
				nm_usuario = wheb_usuario_pck.get_nm_usuario  
		where	nr_sequencia	= nr_seq_episodio_p;

		commit;
	
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
