CREATE OR REPLACE PROCEDURE checar_pontuacao_sepse ( nr_atendimento_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ie_tipo_sepse_p text) AS $body$
DECLARE


ie_tipo_sepse_w     varchar(1);
qt_total_ponto_w	bigint;

C02 CURSOR FOR
	SELECT	nr_seq_atributo, nr_sequencia
	from	escala_sepse_item
	where	nr_seq_escala = nr_sequencia_p
	and     coalesce(ie_tipo_sepse_w,'A') = 'A'
	
union

	SELECT	nr_seq_atributo, nr_sequencia
	from	escala_sepse_infantil_item
	where	nr_seq_escala = nr_sequencia_p
	and     ie_tipo_sepse_w = 'P'
	order by nr_sequencia;

BEGIN

	ie_tipo_sepse_w := obter_se_sepse_liberada(nr_atendimento_p);

	for itens_sepse in C02 loop
		CALL atualizar_pontuacao_sepse(nr_atendimento_p,nr_sequencia_p,itens_sepse.nr_seq_atributo,nm_usuario_p,ie_tipo_sepse_p);
	end loop;

	if (ie_tipo_sepse_w = 'P') then
		select	sum(qt_pontuacao)
		into STRICT	qt_total_ponto_w
		from	escala_sepse_infantil_item
		where	nr_seq_escala = nr_sequencia_p;
		
		if (qt_total_ponto_w > 0) then
			update	escala_sepse_infantil
			set		qt_pontuacao = qt_total_ponto_w
			where	nr_sequencia = nr_sequencia_p;
		end if;
		
	else
		select	sum(qt_pontuacao)
		into STRICT	qt_total_ponto_w
		from	escala_sepse_item
		where	nr_seq_escala = nr_sequencia_p;
		
		if (qt_total_ponto_w > 0) then
			update	escala_sepse
			set		qt_pontuacao = qt_total_ponto_w
			where	nr_sequencia = nr_sequencia_p;
		end if;
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
