CREATE OR REPLACE PROCEDURE checkup_after_post ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

		 
ie_possui_registro	varchar(1) := 'N';
		

BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_possui_registro 
	from	med_avaliacao_paciente 
	where	nr_seq_checkup	= nr_sequencia_p;
	 
	if ('S' = ie_possui_registro) then 
		begin 
		update	checkup 
		set	dt_gerado_aval	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p 
		where	nr_sequencia	= nr_sequencia_p 
		and	coalesce(dt_gerado_aval::text, '') = '';
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
