CREATE OR REPLACE PROCEDURE checkup_liberar_ina_hist ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/*ie_opcao_p Utilizar a mesma para futuras customizações
L = Liberar
I = Inativar*/
BEGIN
if (ie_opcao_p = 'L') then
	update	checkup_hist
	set	dt_liberacao 		= clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
else
	update	checkup_hist
	set	dt_inativacao 		= clock_timestamp(),
		nm_usuario_inativacao 	= nm_usuario_p,
		ie_situacao		= 'I'
	where	nr_sequencia = nr_sequencia_p;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
