SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE check_create_billing_invoice ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, ie_consiste_liberacao_p text, cd_operacao_nf_p bigint, ds_retorno_p INOUT text, ie_obrigatorio_p INOUT text ) AS $body$
DECLARE


qt_ordem_nao_aprov_w		bigint;
qt_nota_fiscal_w		bigint;
cd_estabelecimento_w		smallint;
qt_ordem_compra_w		bigint;
ds_retorno_w			varchar(254);
ie_obrigatorio_w		varchar(1);
qt_existe_w			bigint;
qt_regra_w			bigint;
cd_local_estoque_w		integer;
ie_permite_w			varchar(1) := 'S';
dt_validade_w			timestamp;
ie_varias_nf_oc_w		varchar(1);


BEGIN
ds_retorno_w	 := '';

ie_varias_nf_oc_w := obter_param_usuario(40, 442, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_p, ie_varias_nf_oc_w);

select	count(*)
into STRICT	qt_existe_w
from	ordem_compra
where	nr_ordem_compra	= nr_ordem_compra_p;
if (qt_existe_w > 0) then
	begin


	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	ordem_compra
	where	nr_ordem_compra	= nr_ordem_compra_p;
	
	select 	count(*)
	into STRICT	qt_regra_w
	from	sup_regra_local_nf;
	
	if (qt_regra_w > 0) then
		begin
		select	cd_local_entrega
		into STRICT	cd_local_estoque_w
		from	ordem_compra
		where	nr_ordem_compra = nr_ordem_compra_p;
		
		select	coalesce(obter_se_local_lib_nf(cd_local_estoque_w,coalesce(cd_operacao_nf_p,0)),'S')
		into STRICT	ie_permite_w
		;		
		end;
	end if;
		
	select	count(*)
	into STRICT	qt_ordem_compra_w
	from	ordem_compra
	where	nr_ordem_compra	= nr_ordem_compra_p
	and (coalesce(dt_baixa::text, '') = '' or (nr_seq_motivo_cancel IS NOT NULL AND nr_seq_motivo_cancel::text <> ''));

	select	count(*)
	into STRICT	qt_ordem_nao_aprov_w
	from	ordem_compra
	where	nr_ordem_compra	= nr_ordem_compra_p
	and (coalesce(dt_liberacao::text, '') = '' or coalesce(dt_aprovacao::text, '') = '')
	and	ie_consiste_liberacao_p = 'S';

	select	count(*)
	into STRICT	qt_nota_fiscal_w
	from	nota_fiscal
	where	nr_ordem_compra	= nr_ordem_compra_p;
	
	select	trunc(dt_validade,'dd')
	into STRICT	dt_validade_w
	from	ordem_compra
	where	nr_ordem_compra	= nr_ordem_compra_p;
	
	if (cd_estabelecimento_w <> cd_estabelecimento_p) then
		begin
		ds_retorno_w := wheb_mensagem_pck.get_texto(300017,'NR_ORDEM_COMPRA='||nr_ordem_compra_p||';DS_ESTABELECIMENTO='||substr(obter_nome_estabelecimento(cd_estabelecimento_w),1,80));
		ie_obrigatorio_w := 'S';
		end;
	elsif (dt_validade_w < trunc(clock_timestamp(),'dd')) then
		begin
		ds_retorno_w := wheb_mensagem_pck.get_texto(300018,'NR_ORDEM_COMPRA='||nr_ordem_compra_p);
		ie_obrigatorio_w := 'S';
		end;
	elsif (qt_ordem_compra_w > 0) then
		begin
		ds_retorno_w := wheb_mensagem_pck.get_texto(1138254,'NR_ORDEM_COMPRA='||nr_ordem_compra_p);
		ie_obrigatorio_w := 'S';
		end;
	elsif (qt_nota_fiscal_w > 0) then
		begin
		if (coalesce(ie_varias_nf_oc_w,'S') = 'S') then
			ie_obrigatorio_w := 'N';
			ds_retorno_w := wheb_mensagem_pck.get_texto(300020,'NR_ORDEM_COMPRA='||nr_ordem_compra_p);
		else
			ie_obrigatorio_w := 'S';
			ds_retorno_w := wheb_mensagem_pck.get_texto(300021,'NR_ORDEM_COMPRA='||nr_ordem_compra_p);
		end if;
		end;
	elsif (qt_ordem_nao_aprov_w > 0) then
		begin
		ds_retorno_w := wheb_mensagem_pck.get_texto(300022,'NR_ORDEM_COMPRA='||nr_ordem_compra_p);
		ie_obrigatorio_w := 'S';
		end;
	elsif (ie_permite_w = 'N') then
		begin
		ds_retorno_w := wheb_mensagem_pck.get_texto(300023);
		ie_obrigatorio_w := 'S';
		end;
	end if;
	end;
else
	begin
	ds_retorno_w := wheb_mensagem_pck.get_texto(300024);
	ie_obrigatorio_w := 'S';
	end;
end if;

ds_retorno_p := ds_retorno_w;
ie_obrigatorio_p := ie_obrigatorio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE check_create_billing_invoice ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint, ie_consiste_liberacao_p text, cd_operacao_nf_p bigint, ds_retorno_p INOUT text, ie_obrigatorio_p INOUT text ) FROM PUBLIC;

