SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE check_encounter_linked ( nr_sequencia_p bigint, cd_tipo_agenda_p bigint) AS $body$
DECLARE


dt_cancelamento_w       atendimento_paciente.dt_cancelamento%type;


BEGIN

	if ( cd_tipo_agenda_p = 3 ) then
		select	max(a.dt_cancelamento)
        into STRICT	dt_cancelamento_w
        from 	atendimento_paciente a,
				agenda_consulta b
        where 	a.nr_atendimento = b.nr_atendimento
        and 	b.nr_sequencia = nr_sequencia_p;

        if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
            update 	agenda_consulta
            set 	nr_atendimento  = NULL
            where 	nr_sequencia = nr_sequencia_p;
            commit;
        end if;

	elsif ( cd_tipo_agenda_p = 2 ) then
		select 	max(a.dt_cancelamento)
		into STRICT 	dt_cancelamento_w
		from  	atendimento_paciente a,
			agenda_paciente b
		where 	a.nr_atendimento = b.nr_atendimento
		and 	b.nr_sequencia = nr_sequencia_p;

        if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
            update 	agenda_paciente
            set 	nr_atendimento  = NULL
            where 	nr_sequencia = nr_sequencia_p;
            commit;
        end if;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
