CREATE OR REPLACE PROCEDURE check_if_datamodel_exists ( data_model_id_p data_model.nr_sequencia%TYPE ) AS $body$
DECLARE


    data_model_id_w  data_model.nr_sequencia%TYPE;
    is_present bigint;

BEGIN
    data_model_id_w := data_model_id_p;
    SELECT max(data_model_id)
    INTO STRICT is_present
    FROM
        data_model_columns
    WHERE
        data_model_id = data_model_id_w;
    if (is_present IS NOT NULL AND is_present::text <> '') then
        EXECUTE 'DELETE FROM data_model_columns WHERE DATA_MODEL_ID = ' || data_model_id_w;
        COMMIT;
    END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
