CREATE OR REPLACE PROCEDURE check_opsm_integration (nr_seq_agenda_p bigint, ie_tipo_regra_p text, ie_opme_integracao_p text, ie_msg_envio_agendamento_p INOUT text, ie_msg_envio_itens_p INOUT text) AS $body$
DECLARE


ie_utiliza_integracao_w		varchar(1) := 'N';
ie_solicita_integracao_w 	varchar(1) := 'N';
ie_envia_ci_w				varchar(1) := 'N';
ie_envia_integracao_w		varchar(1);
nm_usuario_w				varchar(15);
cd_estabelecimento_w		smallint;
cd_perfil_w					bigint;
ie_msg_envio_agendamento_w	varchar(1) := 'N';
ie_msg_envio_itens_w		varchar(1) := 'N';


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	cd_perfil_w := wheb_usuario_pck.get_cd_perfil;
	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

	ie_utiliza_integracao_w := obter_param_usuario(871, 257, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_utiliza_integracao_w);
	ie_solicita_integracao_w := obter_param_usuario(871, 467, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_solicita_integracao_w);
	ie_envia_ci_w := obter_param_usuario(871, 717, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_envia_ci_w);

	if (ie_utiliza_integracao_w = 'D') then
		select 	coalesce(max(obter_envio_auto_int_opme(cd_estabelecimento_w, nr_seq_agenda_p, ie_tipo_regra_p)), 'N')
		into STRICT 	ie_envia_integracao_w
		;

		if (ie_solicita_integracao_w <> 'N' and coalesce(ie_opme_integracao_p,'N') <> 'S') then
			if (ie_solicita_integracao_w = 'Q' and ie_envia_integracao_w <> 'N') then
				ie_msg_envio_agendamento_w := 'Q';
			elsif (ie_solicita_integracao_w = 'S') then
				ie_msg_envio_agendamento_w := 'S';
			end if;
		end if;

		if (ie_envia_integracao_w = 'S') then
			ie_msg_envio_itens_w := 'S';
		elsif (ie_envia_integracao_w = 'P') then
			ie_msg_envio_itens_w := 'Q';
		elsif (ie_envia_ci_w = 'S') then
			ie_msg_envio_itens_w := 'S';
		end if;
	end if;
end if;

ie_msg_envio_agendamento_p 	:= ie_msg_envio_agendamento_w;
ie_msg_envio_itens_p 		:= ie_msg_envio_itens_w;

end;
$body$
