CREATE OR REPLACE PROCEDURE cih_copiar_lib_def_infec_presc ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, qt_dia_lib_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
