qt_reg_w	bigint;


BEGIN
select	count(1)
into STRICT	qt_reg_w
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_prescr_p  LIMIT 1;

if (qt_reg_w = 1) then
	update	prescr_material
	set	qt_dias_liberado	= qt_dia_lib_p
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= nr_seq_prescr_p;
end if;

commit;

end;
