CREATE OR REPLACE PROCEDURE cih_encerrar_ficha_ocorrencia ( nr_ficha_ocorrencia_p bigint, ie_encerrar_p text, nm_usuario_p text) AS $body$
BEGIN
if (ie_encerrar_p = 'S') then

	update	cih_ficha_ocorrencia
	set	dt_encerramento		= clock_timestamp(),
		nm_usuario_encerramento = nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_ficha_ocorrencia	= nr_ficha_ocorrencia_p;

end if;

if (ie_encerrar_p = 'N') then

	update	cih_ficha_ocorrencia
	set	dt_encerramento		 = NULL,
		nm_usuario_encerramento  = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_ficha_ocorrencia	= nr_ficha_ocorrencia_p;

end if;

commit;

end;
$body$
