SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cih_enviar_email_pos_alta_med ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
ds_email_remetente_w	varchar(100);
ds_email_destino_w		varchar(100);
ds_assunto_w			varchar(100);
ds_mensagem_w			varchar(4000);
ds_texto_w				varchar(32000);
ds_texto2_w				varchar(32000);
ds_texto_paciente_w		varchar(1000);
nm_pessoa_fisica_w		varchar(255);
cd_pessoa_fisica_w		varchar(10);
nm_medico_cirurgiao_w	varchar(255);
cd_medico_cirurgiao_w	varchar(10);
ie_envio_w				varchar(1);
ds_procedimento_w		procedimento.ds_procedimento%TYPE;
dt_cirurgia_w			timestamp;
dt_nascimento_w			timestamp;
					
C01 CURSOR FOR 
	SELECT	DISTINCT 
		b.cd_medico_cirurgiao, 
		SUBSTR(obter_nome_pf(b.cd_medico_cirurgiao),1,255) nm_pessoa_fisica 
	from	atendimento_paciente a, 
		cirurgia b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '') 
	and 	b.ie_status_cirurgia = '2' 
	and	a.dt_alta between inicio_dia(dt_referencia_p) and fim_dia(last_day(dt_referencia_p)) 
	order by 2;
					
C02 CURSOR FOR 
	SELECT	a.cd_pessoa_fisica, 
		substr(obter_nome_pf(a.cd_pessoa_fisica),1,255) nm_pessoa_fisica, 
		a.nr_atendimento, 
		substr(obter_exame_agenda(b.cd_procedimento_princ, b.ie_origem_proced, b.nr_seq_proc_interno),1,240) ds_procedimento, 
		b.dt_inicio_real, 
		obter_dados_pf(a.cd_pessoa_fisica,'DN') 
	from	atendimento_paciente a, 
		cirurgia b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '') 
	and 	b.ie_status_cirurgia = '2' 
	and	b.cd_medico_cirurgiao = cd_medico_cirurgiao_w 
	and	obter_se_alta_obito(a.nr_atendimento) = 'N' 
	and	a.dt_alta between inicio_dia(dt_referencia_p) and fim_dia(last_day(dt_referencia_p)) 
	order by 2;
			

BEGIN 
 
select	max(ds_remetente_email), 
	max(ds_assunto), 
	max(ds_mensagem), 
	max(ds_texto_paciente) 
into STRICT	ds_email_remetente_w, 
	ds_assunto_w, 
	ds_mensagem_w, 
	ds_texto_paciente_w 
from	cih_parametros 
where (cd_estabelecimento = cd_estabelecimento_p 
or (coalesce(cd_estabelecimento::text, '') = '' 
and not exists (SELECT	1 
		from	cih_parametros 
		where	cd_estabelecimento = cd_estabelecimento_p)));
 
open C01;
loop 
fetch C01 into	 
	cd_medico_cirurgiao_w, 
	nm_medico_cirurgiao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	max(ds_email_ccih) 
	into STRICT	ds_email_destino_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica = cd_medico_cirurgiao_w;
	 
	ds_texto2_w := '';
	 
	open C02;
	loop 
	fetch C02 into	 
		cd_pessoa_fisica_w, 
		nm_pessoa_fisica_w, 
		nr_atendimento_w, 
		ds_procedimento_w, 
		dt_cirurgia_w, 
		dt_nascimento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		ds_texto2_w := substr(ds_texto2_w||chr(13)||chr(10)||chr(13)||chr(10)|| wheb_mensagem_pck.get_texto(800315) ||': '||nr_atendimento_w||' - '|| wheb_mensagem_pck.get_texto(800317) ||': '||dt_nascimento_w||' - '|| wheb_mensagem_pck.get_texto(800318) ||': '||dt_cirurgia_w||' - '|| wheb_mensagem_pck.get_texto(800319) ||' '||nm_pessoa_fisica_w||chr(13)||chr(10)||ds_texto_paciente_w,1,32000);
		 
		end;
	end loop;
	close C02;
	 
	ds_texto_w := substr(ds_mensagem_w||ds_texto2_w,1,32000);
	ds_texto_w := substr(replace(ds_texto_w,'@medico',nm_medico_cirurgiao_w),1,32000);
	 
	if (ds_email_destino_w IS NOT NULL AND ds_email_destino_w::text <> '') then 
		CALL Enviar_Email(ds_assunto_w,ds_texto_w,ds_email_remetente_w,ds_email_destino_w,nm_usuario_p,'M');
		CALL Gerar_log_envio_pos_alta(cd_pessoa_fisica_w, nr_atendimento_w, '', ds_email_destino_w, nm_usuario_p);
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cih_enviar_email_pos_alta_med ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, nm_usuario_p text) FROM PUBLIC;

